        if (reset) begin
            // When reset is high, set the counter back to 0
            q <= 4'b0000;
        end
        else if (q == 4'b1001) begin
            // If the counter is at 9, wrap back to 0 on the next clock
            q <= 4'b0000;
        end
        else begin
            // Otherwise, just increment the counter
            q <= q + 1'b1;
        end
    end

endmodule

