    input clk,
    input areset,
    input x,
    output z
); 
    parameter A=0, B=1, C=2, D=3, E=4, F=5;
    reg [2:0] PS, NS;

    assign z = ((PS == E) || (PS == F))? 1:0;

    always @(posedge clk, posedge areset) begin
        if(areset)
            PS <= A;
        else
            PS <= NS;
    end

    always @(PS) begin
        case(PS)
            A: NS <= (x)? B:A;
            B: NS <= (x)? B:C;
            C: NS <= (x)? D:A;
            D: NS <= (x)? B:C;
            E: NS <= (x)? E:F;
            F: NS <= (x)? D:A;
            default: NS <= PS;
        endcase
    end

endmodule

