// Design a finite state machine that will identify when bytes have been correctly received when given a stream of bits. It needs to identify the start bit, wait for all 8 data bits, then verify that the stop bit was correct. If the stop bit does not appear when expected, the FSM must wait until it finds a stop bit before attempting to receive the next byte.

module top_module(
    input clk,
    input in,
    input reset,    // Synchronous reset
    output done
); 

    parameter START = 0, DATA = 1, STOP = 2, DELAY = 3;
    reg [1:0] state, next_state;
    reg [3:0] count;
    reg out;

    always @(*) begin
        case(state)
            START: begin
                if(in)
                    next_state = DATA;
                else
                    next_state = START;
            end
            DATA: begin
                if(count < 9)
                    next_state = DATA;
                else
                    next_state = STOP;
            end
            STOP: begin
                if(in)
                    next_state = START;
                else
                    next_state = DELAY;
            end
            DELAY: begin
                if(count < 20)
                    next_state = DELAY;
                else
                    next_state = START;
            end
        endcase
    end

    always @(posedge clk) begin
        if(reset)
            state <= START;
        else
            state <= next_state;
    end

    always @(posedge clk) begin
        if(state == DATA)
            count <= count + 1;
        else
            count <= 0;
    end

    always @(*) begin
        case(state)
            START: out = 0;
            DATA: out = count < 9;
            STOP: out = count < 20;
            DELAY: out = 0;
        endcase
    end

    assign done = out;

endmodule

