// Note that the serial protocol sends the least significant bit first.

// Hint: The serial bitstream needs to be shifted in one bit at a time, then read out in parallel. 
// Use FSM from Fsm_serial
// New: Datapath to latch input bits.

module top_module(
    input clk,
    input in,
    input reset,    // Synchronous reset
    output [7:0] out_byte,
    output done
);

    // Modify FSM and datapath from Fsm_serial

    parameter idle = 0, rec_data = 1, stop_bit = 2, error = 3;
    reg [2:0] state, next_state;
    reg [7:0] data;
    reg [3:0] counter;
    reg out;

    always @(posedge clk) begin
        if(reset)
            state <= idle;
        else
            state <= next_state;
    end

    always @(*) begin
        case(state)
            idle: begin
                if(~in)
                    next_state = rec_data;
                else
                    next_state = idle;
            end
            rec_data: begin
                if(counter == 7)
                    next_state = stop_bit;
                else
                    next_state = rec_data;
            end
            stop_bit: begin
                if(in)
                    next_state = idle;
                else
                    next_state = error;
            end
            error: begin
                if(in)
                    next_state = idle;
                else
                    next_state = error;
            end
        endcase
    end

    always @(posedge clk) begin
        if(state == rec_data)
            counter <= counter + 1;
        else
            counter <= 0;
    end

    always @(posedge clk) begin
        if(state == stop_bit)
            data <= {data[6:0], out};
    end

    always @(posedge clk) begin
        if(state == rec_data && counter == 7)
            out <= in;
    end

    assign done = (state == stop_bit)? 1:0;
    assign out_byte = data;

endmodule

