// Each output bit behaves like a SR flip-flop: The output bit should be set (to 1) the cycle after a 1 to 0 transition occurs. The output bit should be reset (to 0) at the positive clock edge when reset is high. If both of the above events occur at the same time, reset has precedence. In the last 4 cycles of the example waveform below, the'reset' event occurs one cycle earlier than the'set' event, so there is no conflict here.

module top_module (
    input clk,
    input reset,
    input [31:0] in,
    output [31:0] out
);
    reg [31:0] reg_a, reg_b, reg_c, reg_d, reg_e, reg_f, reg_g, reg_h;

    // This initial block is only used to initialize
    // all of the registers to a known set of values.
    initial
    begin
        reg_a = 32'h00ff00ff;
        reg_b = 32'h11111111;
        reg_c = 32'h22222222;
        reg_d = 32'h33333333;
        reg_e = 32'h44444444;
        reg_f = 32'h55555555;
        reg_g = 32'h66666666;
        reg_h = 32'h77777777;
    end

    // At each clock, the value of each
    // register is updated based on the
    // input.
    always @(posedge clk)
    begin
        reg_a <= in;
        reg_b <= in;
        reg_c <= in;
        reg_d <= in;
        reg_e <= in;
        reg_f <= in;
        reg_g <= in;
        reg_h <= in;
    end

    // The value of out is the value
    // of the register that was written
    // to the most recently
    // updated register.
    assign out = reg_h;
endmodule

